INSERT INTO `movie_info` (`id`,`title`,`directors`,`screenwriters`,`types`,`nations`,`languages`,`releaseDate`,`year`,`duration`,`actors`,`knownAs`,`doubanId`,`imdbId`,`posterUrl`,`star`,`rate`,`votesNum`,`fiveStarRatio`,`fourStarRatio`,`threeStarRatio`,`twoStarRatio`,`oneStarRatio`,`summary`,`posterX`,`posterY`,`doubanUrl`,`createdTime`,`updateTime`,`playLinks`) VALUES (363,'生活多美好','[\"弗兰克·卡普拉\"]','[\"弗朗西丝·古德里奇\", \"艾伯特·哈克特\", \"弗兰克·卡普拉\", \"乔·斯沃林\", \"Philip Van Doren Stern\", \"迈克尔·威尔逊\"]','[\"剧情\", \"爱情\", \"奇幻\"]',' 美国',' 英语 / 法语','[\"1946-12-20\"]','(1946)','130 分钟','[\"詹姆斯·斯图尔特\", \"唐娜·里德\", \"莱昂纳尔·巴里摩尔\", \"托马斯·米切尔\", \"亨利·崔佛斯\"]',' 风云人物 / 美好人生 / 哀乐人生 / 美好生活 / Frank Capra\'s It\'s a Wonderful Life',1293749,'tt0038650','https://img1.doubanio.com/view/photo/s_ratio_poster/public/p771490367.jpg',45,92,42022,'68.4%','25.7%','5.3%','0.4%','0.2%','\n                                　　乔治（詹姆斯•斯图尔特 James Stewart 饰）在圣诞节前准备自杀，这时上帝传来旨意，派天使拯救他，并让他了解到自己一生的使命——拯救那些不幸的人。乔治小时候左耳有疾，在贝德福德镇上的一家药店帮工。他很机灵，深得老板器重，同时也很招女孩喜欢。某日，老板接到爱子死亡的电报，悲痛欲绝，配错了药，幸亏乔治才化险为夷。乔治从小嫉恶如仇，尤其看不惯富人专横跋扈。长大后，乔治一表人材，准备去读大学。这时，他的父亲去世了，他子承父业，协助叔父料理公司事务。然而公司经营状况不佳，在董事会会议上面临破产。如果公司破产，小镇将会有很多家庭陷入困境。乔治力排众议，说服了董事会，但是条件是他要担任董事会秘书长。这与他的大学梦相去甚远。而且，此时他还陷入了对一个女孩的爱恋之中……\n                        ',1000,1500,'https://movie.douban.com/subject/1293749/','2019-02-21 14:15:10','2019-02-21 14:15:10','[{\"腾讯视频\": \"https://www.douban.com/link2/?url=http%3A%2F%2Fv.qq.com%2Fx%2Fcover%2F4nykax6327kt6sw.html%3Fptag%3Ddouban.movie&subtype=1&type=online-video\"}, {\"搜狐视频\": \"https://www.douban.com/link2/?url=http%3A%2F%2Ffilm.sohu.com%2Falbum%2F9174230.html&subtype=2&type=online-video\"}, {\"PP视频\": \"https://www.douban.com/link2/?url=http%3A%2F%2Fv.pptv.com%2Fshow%2FfWAqqRF35yWIBm4.html%3Frcc_id%3Ddb_zxsp&subtype=10&type=online-video\"}]');
INSERT INTO `movie_info` (`id`,`title`,`directors`,`screenwriters`,`types`,`nations`,`languages`,`releaseDate`,`year`,`duration`,`actors`,`knownAs`,`doubanId`,`imdbId`,`posterUrl`,`star`,`rate`,`votesNum`,`fiveStarRatio`,`fourStarRatio`,`threeStarRatio`,`twoStarRatio`,`oneStarRatio`,`summary`,`posterX`,`posterY`,`doubanUrl`,`createdTime`,`updateTime`,`playLinks`) VALUES (364,'福音战士新剧场版：破','[\"摩砂雪\", \"鹤卷和哉\", \"庵野秀明\"]','[\"庵野秀明\"]','[\"剧情\", \"科幻\", \"动画\"]',' 日本',' 日语','[\"2009-06-27\"]','(2009)','108分钟','[\"绪方惠美\", \"三石琴乃\", \"宫村优子\", \"立木文彦\", \"山口由里子\"]',' Evangelion: 2.0 You Can (Not) Advance / Evangelion New Theatrical Version: Breaking',2567646,'tt0860906','https://img3.doubanio.com/view/photo/s_ratio_poster/public/p1026522114.jpg',45,93,42933,'70.6%','22.8%','5.8%','0.5%','0.3%','\n                                　　在参拜母亲坟墓的回程途中，真嗣（绪方惠美 配音）和葛城美里（三石琴乃 配音）遭遇了第七使徒的攻击，关键时刻2号机势如破竹挫败对手，真嗣也由此结识了2号机的操纵者式波·明日香·兰格蕾（宫村优子 配音）。与沉默寡言的绫波丽（林原惠美 配音）不同，明日香自信张扬，甚至有些自大，在和真嗣相处的日子里，彼此闹出不少的笑话。他们三人分别驾驶着零号机、初号机和2号机，抵抗了第八使徒猛烈的攻击，在这一过程中，淡淡的情愫悄然在这三个青年男女的心中萌生。不久后，第九、十使徒相继降临，真嗣他们面临着生与死的残酷考验……\n                                    ',2144,3028,'https://movie.douban.com/subject/2567646/','2019-02-21 14:15:23','2019-02-21 14:15:23','[{\"哔哩哔哩\": \"javascript: void 0;\"}]');
INSERT INTO `movie_info` (`id`,`title`,`directors`,`screenwriters`,`types`,`nations`,`languages`,`releaseDate`,`year`,`duration`,`actors`,`knownAs`,`doubanId`,`imdbId`,`posterUrl`,`star`,`rate`,`votesNum`,`fiveStarRatio`,`fourStarRatio`,`threeStarRatio`,`twoStarRatio`,`oneStarRatio`,`summary`,`posterX`,`posterY`,`doubanUrl`,`createdTime`,`updateTime`,`playLinks`) VALUES (365,'爱·回家','[\"李廷香\"]','[\"李廷香\"]','[\"剧情\", \"家庭\"]',' 韩国',' 韩语','[\"2002-04-05(韩国)\"]','(2002)','80分钟','[\"金艺芬\", \"俞承豪\"]',' 外婆的家 / 回家路上 / 有你真好 / 回家路 / 一屋两婆孙 / The Way Home',1299361,'tt0312841','https://img3.doubanio.com/view/photo/s_ratio_poster/public/p2107418134.jpg',45,90,54217,'62.2%','29.3%','7.5%','0.7%','0.3%','\n                                　　为了方便找工作，妈领着7岁的相宇（俞承浩饰）去乡下的外婆家，想把他暂时留在那里。外婆（金亦芬饰）年事已高，不能说话也不识字。而一直生活在城市里的孙子相宇，刚开始无法适应连游戏机、电池都没得卖的乡下生活。他开始蛮横地流露出不满，抱怨这个抱怨那。为了买电池，偷走外婆的银头簪拿去卖；外婆得知孙子想吃炸鸡，却只理解到鸡肉，炖了只鸡给孙子吃，却被生气的相宇踢翻了。\n                                    ',520,745,'https://movie.douban.com/subject/1299361/','2019-02-21 14:15:36','2019-02-21 14:15:36','null');
