INSERT INTO `movie_info` (`id`,`title`,`directors`,`screenwriters`,`types`,`nations`,`languages`,`releaseDate`,`year`,`duration`,`actors`,`knownAs`,`doubanId`,`imdbId`,`posterUrl`,`star`,`rate`,`votesNum`,`fiveStarRatio`,`fourStarRatio`,`threeStarRatio`,`twoStarRatio`,`oneStarRatio`,`summary`,`posterX`,`posterY`,`doubanUrl`,`createdTime`,`updateTime`,`playLinks`) VALUES (367,'黄金三镖客','[\"赛尔乔·莱昂内\"]','[\"阿杰诺雷·因克罗奇\", \"富里奥·斯卡佩利\", \"卢恰诺·温琴佐尼\", \"赛尔乔·莱昂内\"]','[\"西部\", \"冒险\"]',' 意大利 / 西班牙 / 西德',' 意大利语 / 英语','[\"1966-12-23(意大利)\"]','(1966)','161 分钟','[\"克林特·伊斯特伍德\", \"埃里·瓦拉赫\", \"李·范·克里夫\", \"阿尔多·久弗瑞\", \"路易吉·皮斯蒂利\"]',' 好·坏·丑 / 独行侠决斗地狱门(港) / 黄昏三镖客(台) / 好家伙，坏家伙，丑家伙 / 善恶丑 / The Good, the Bad and the Ugly',1401118,'tt0060196','https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2101693489.jpg',45,91,64674,'64.0%','28.9%','6.3%','0.6%','0.2%','\n                                　　故事发生在美国南北战争时期。图科（埃里•瓦拉赫 Eli Wallach 饰）是一个图财害命的江洋大盗，因此他被镇上悬赏通缉。布兰迪（克林特•伊斯特伍德 Clint Eastwood  饰）是一个除暴安良的牛仔，他无意中抓住了图科，但嫌赏金不够又掳走了他。在荒漠中，布兰迪惩罚图科让其自生自灭。但是诡诈的图科居然逃过了一劫，并纠集一些帮凶在客栈捉住了布兰迪。正当图科以牙还牙折磨布兰迪的时候，他劫持了一个名叫卡森的士兵。后者临死前留下了宝藏的秘密，图科和布兰迪分别获得了一半信息。与此同时，一个狡猾的杀手桑坦萨（李•范•克里夫 Lee Van Cleef 饰）也通过其他渠道发现了宝藏的秘密。于是，在寻宝的道路上，三个人使出浑身解数，上演了一场场对决的好戏……\n                        ',450,635,'https://movie.douban.com/subject/1401118/','2019-02-21 14:16:04','2019-02-21 14:16:04','null');
INSERT INTO `movie_info` (`id`,`title`,`directors`,`screenwriters`,`types`,`nations`,`languages`,`releaseDate`,`year`,`duration`,`actors`,`knownAs`,`doubanId`,`imdbId`,`posterUrl`,`star`,`rate`,`votesNum`,`fiveStarRatio`,`fourStarRatio`,`threeStarRatio`,`twoStarRatio`,`oneStarRatio`,`summary`,`posterX`,`posterY`,`doubanUrl`,`createdTime`,`updateTime`,`playLinks`) VALUES (368,'我爱你','[\"秋昌旼\", \"孙昌敏\"]','[\"姜草\", \"秋昌旼\"]','[\"剧情\", \"爱情\"]',' 韩国',' 韩语','[\"2011-02-17(韩国)\"]','(2011)','118分钟','[\"宋在浩\", \"李顺载\", \"尹秀晶\", \"金秀美\", \"宋智孝\"]',' 爱你 / 爱，是一生相伴(台) / Late Blossom',5908478,'tt1862020','https://img1.doubanio.com/view/photo/s_ratio_poster/public/p1075591188.jpg',45,90,75207,'60.5%','31.0%','7.6%','0.6%','0.2%','\n                                　　飘雪的坡道上，送牛奶的老头子金万皙（李顺载 饰）邂逅了收废品的老太太宋怡芬（尹秀晶 饰）。他们的前半生各自背负着艰辛的人生：万皙年轻时我行我素，直到妻子病逝才深觉愧疚；怡芬被心爱的男人抛弃在城市角落，孩子夭折后至老未嫁。两位老人在每天凌晨寂静的街道上相遇，孤独而苦难的生活令他们渐渐靠近。在万皙孙女妍儿（宋智孝 饰）的穿针引线下，如初恋般的悸动萌发在了彼此心中。万皙的朋友、停车场管理员张军峰（宋在河 饰）一直耐心的看护着患有老年痴呆症的老伴。直到有一天，军峰得知老伴已患绝症。目睹她一次次痛苦病发，心如刀绞的军峰做出了悲伤的决定……\n                                    ',1343,1913,'https://movie.douban.com/subject/5908478/','2019-02-21 14:16:17','2019-02-21 14:16:17','[{\"腾讯视频\": \"https://www.douban.com/link2/?url=http%3A%2F%2Fv.qq.com%2Fx%2Fcover%2F6yyurkhdbqna5px.html%3Fptag%3Ddouban.movie&subtype=1&type=online-video\"}]');
