INSERT INTO `movie_info` (`id`,`title`,`directors`,`screenwriters`,`types`,`nations`,`languages`,`releaseDate`,`year`,`duration`,`actors`,`knownAs`,`doubanId`,`imdbId`,`posterUrl`,`star`,`rate`,`votesNum`,`fiveStarRatio`,`fourStarRatio`,`threeStarRatio`,`twoStarRatio`,`oneStarRatio`,`summary`,`posterX`,`posterY`,`doubanUrl`,`createdTime`,`updateTime`,`playLinks`) VALUES (374,'雨中曲','[\"斯坦利·多南\", \"吉恩·凯利\"]','[\"贝蒂·康登\", \"阿多夫·格林\"]','[\"喜剧\", \"爱情\", \"歌舞\"]',' 美国',' 英语','[\"1952-04-11(美国)\"]','(1952)','103分钟','[\"吉恩·凯利\", \"唐纳德·奥康纳\", \"黛比·雷诺斯\", \"简·哈根\", \"米勒德·米切尔\"]',' 雨中情 / 万花嬉春 / 百花嬉春',1293460,'tt0045152','https://img3.doubanio.com/view/photo/s_ratio_poster/public/p1612355875.jpg',45,90,113179,'57.5%','34.5%','7.5%','0.4%','0.1%','\n                                　　无声电影时代，演员的形体远远比声音重要。琳娜（简·哈根 Jean Hagen 饰）就是凭着讨好的外形，和唐（吉恩·凯利 Gene Kelly 饰）成为大受欢迎的明星搭档。然而，随着技术的发展，有声电影问世，这就难倒了声音及其难听的琳娜。无论如何训练，她都不能把台词念得漂 亮。于是，唐请来了声音甜美的凯西（黛比·雷诺斯 Debbie Reynolds 饰），让她做琳娜音乐剧幕后的配音者，令琳娜的演出声名大振。而走红的琳娜更加目中无人。\n                                    ',1046,1500,'https://movie.douban.com/subject/1293460/','2019-02-21 14:17:35','2019-02-21 14:17:35','[{\"优酷视频\": \"https://www.douban.com/link2/?url=http%3A%2F%2Fcps.youku.com%2Fredirect.html%3Fid%3D0000a213%26url%3Dhttp%3A%2F%2Fv.youku.com%2Fv_show%2Fid_XNzc0MjU5NDQ4.html&subtype=3&type=online-video\"}, {\"1905电影网\": \"https://www.douban.com/link2/?url=http%3A%2F%2Fvip.1905.com%2Fplay%2F1330171.shtml%3F__hz%3D6e0721b2c6977135&subtype=13&type=online-video\"}]');
