INSERT INTO `movie_info` (`id`,`title`,`directors`,`screenwriters`,`types`,`nations`,`languages`,`releaseDate`,`year`,`duration`,`actors`,`knownAs`,`doubanId`,`imdbId`,`posterUrl`,`star`,`rate`,`votesNum`,`fiveStarRatio`,`fourStarRatio`,`threeStarRatio`,`twoStarRatio`,`oneStarRatio`,`summary`,`posterX`,`posterY`,`doubanUrl`,`createdTime`,`updateTime`,`playLinks`) VALUES (398,'镜子','[\"安德烈·塔可夫斯基\"]','[\"亚历山大·米沙林\", \"安德烈·塔可夫斯基\"]','[\"剧情\", \"传记\", \"历史\", \"战争\"]',' 苏联',' 俄语 / 西班牙语','[\"1975-03-07(苏联)\"]','(1975)','108 分钟','[\"玛格丽特·捷列霍娃\", \"因诺肯季·斯莫克图诺夫斯基\", \"奥列格·扬科夫斯基\", \"菲利普·扬科夫斯基\", \"伊格纳特·达尼利采夫\"]',' 写真 / Zerkalo / The Mirror',1299248,'tt0072443','https://img3.doubanio.com/view/photo/s_ratio_poster/public/p2327630533.jpg',45,90,12144,'60.5%','29.3%','8.1%','1.5%','0.7%','\n                                　　本片是导演安德烈·塔科夫斯基（Andrei Tarkovsky）的一部自传体电影。幼年的塔科夫斯基随着母亲（玛格瑞塔·泰瑞柯娃 Margarita Terekhova 饰）去亲戚家借钱，他独自在一个陌生的房间里发现了一面镜子，并通过镜子审视自己，在巴赫的音乐中，塔可夫斯基的回忆慢慢带出了一系列的事件：电视上在播报一个口吃的年轻人通过催眠治痊了自己；一个被家庭忽视的妻子遇上一位迷路的乡村医生，两人之间产生若即若离的关系；一个未曾露面的叙述者与前妻在争吵；一个早熟的年轻人备受军事指导员的呵责。这些毫无关联的片段式回忆通过镜子串联起来，展现了导演对时间、历史、生活、土地、梦境等意象的哲学思考。\n                                    ',1000,1500,'https://movie.douban.com/subject/1299248/','2019-02-21 14:23:47','2019-02-21 14:23:47','null');
